html:
{% set css %}
<style>
    .pmt-wrapper {
        font-family: "Helvetica", "Arial", sans-serif;
        color: #222;
        font-size: 12px;
        padding: 26px;
        border: 1px solid #d9d9d9;
        border-radius: 12px;
        box-shadow: 0 6px 18px rgba(0, 0, 0, 0.08);
        background: #fff;
    }
    .pmt-header {
        text-align: center;
        margin-bottom: 20px;
    }
    .logo-wrap {
        margin-bottom: 10px;
    }
    .logo-wrap img {
        max-height: 140px;
        max-width: 440px;
        object-fit: contain;
    }
    .pmt-header h1 {
        font-size: 24px;
        margin: 0;
        letter-spacing: 1px;
        font-weight: 700;
        text-transform: uppercase;
    }
    .pmt-header .doc-name {
        margin: 6px 0 0;
        font-size: 12px;
        color: #777;
        text-transform: uppercase;
        letter-spacing: .5px;
    }
    .master-grid {
        display: grid;
        grid-template-columns: repeat(auto-fit, minmax(160px, 1fr));
        gap: 12px 20px;
        margin-bottom: 24px;
    }
    .master-field {
        padding: 12px;
        border: 1px solid #ececec;
        border-radius: 8px;
        background: #fbfbfb;
        min-height: 72px;
    }
    .master-field label {
        font-weight: 700;
        display: block;
        margin-bottom: 4px;
        font-size: 12px;
        color: #555;
        letter-spacing: .7px;
        text-transform: uppercase;
    }
    .master-field .value {
        font-size: 18px;
        color: #111;
        font-weight: 600;
        text-transform: uppercase;
    }
    .status-badge {
        display: inline-flex;
        align-items: center;
        justify-content: center;
        padding: 6px 12px;
        border-radius: 999px;
        font-weight: 700;
        letter-spacing: 1px;
    }
    .status-badge.solvente {
        background: #e6f4ea;
        color: #1b7f33;
        border: 1px solid rgba(27, 127, 51, 0.3);
    }
    .status-badge.insolvente {
        background: #fdecea;
        color: #c82333;
        border: 1px solid rgba(200, 35, 51, 0.35);
    }
    .status-badge.desconocido {
        background: #f6f6f6;
        color: #777;
        border: 1px solid #d2d2d2;
    }
    .detalle-multas {
        margin-top: 28px;
        border-top: 1px solid #eee;
        padding-top: 20px;
    }
    .empty-detail {
        padding: 20px;
        border: 1px dashed #ccc;
        border-radius: 6px;
        text-align: center;
        color: #666;
        background: #fafafa;
        font-weight: 600;
        text-transform: uppercase;
        letter-spacing: .4px;
    }
    .detalle-multas table {
        width: 100%;
        border-collapse: collapse;
        margin-top: 12px;
        font-size: 12px;
    }
    .detalle-multas thead {
        background: #f7f7f7;
    }
    .detalle-multas tbody tr {
        border-bottom: 1px dashed #c6c6c6;
    }
    .detalle-multas tbody tr:last-child {
        border-bottom: none;
    }
    .detalle-multas th,
    .detalle-multas td {
        padding: 6px 8px;
        text-align: left;
        border: none;
    }
</style>
{% endset %}

{{ css | safe }}

{% set solvency_label = (doc.es_solvente or _('Desconocido')).upper() %}
{% set badge_class = 'solvente' if solvency_label == 'SOLVENTE' else 'insolvente' if solvency_label == 'INSOLVENTE' else 'desconocido' %}

<div class="pmt-wrapper">
    <div class="pmt-header">
        <div class="logo-wrap">
            <img src="{{ frappe.utils.get_url('/files/LOGOMVC.jpeg') }}" alt="Logo">
        </div>
        <h1>DESPLEGADO DE MULTAS DE TRÁNSITO</h1>
        {% if doc.name %}
            <p class="doc-name">{{ doc.name }}</p>
        {% endif %}
    </div>

    <div class="master-grid">
        <div class="master-field">
            <label>{{ _('Placa') }}</label>
            <div class="value">{{ doc.placa_vehiculo_buscar or _('Sin placa registrada') }}</div>
        </div>
        <div class="master-field">
            <label>{{ _('Marca') }}</label>
            <div class="value">{{ doc.marca_vehiculo or _('Sin marca registrada') }}</div>
        </div>
        <div class="master-field">
            <label>{{ _('Estado de Solvencia') }}</label>
            <div class="value"><span class="status-badge {{ badge_class }}">{{ solvency_label }}</span></div>
        </div>
    </div>

    <div class="detalle-multas">
        {% set detalle_html = doc.multas_db or doc.detalle_multas %}
        {% if detalle_html %}
            {{ detalle_html | safe }}
        {% else %}
            <div class="empty-detail">{{ _('No hay multas registradas.') }}</div>
        {% endif %}
    </div>
</div>
